mapper.update:
 		UPDATE 
 			department 
 		SET 
			col_cd = #{colCd}
			,flct_no = #{flctNo}
			,dept_nm = #{deptNm}
			,dept_telno = #{deptTelno}
			,dept_otl = #{deptOtl}
			,dept_goal = #{deptGoal}
		WHERE
 			 dept_cd = #{deptCd}

 		UPDATE 
 			subject 
 		SET 
			dept_cd = #{deptCd}
			,sub_nm = #{subNm}
			,sub_grade = #{subGrade}
			,sub_hour = #{subHour}
			,sub_crd = #{subCrd}
			,crs_classf_cd = #{crsClassfCd}
			,sub_otl = #{subOtl}
		WHERE
 			 sub_no = #{subNo}

