mapper.update:
UPDATE COURSE_REGIST
SET GRD_DMR_CN = #{grdDmrCn},
    GRD_DMR_STTS_CD = 'wait'
WHERE STD_NO = #{stdNo}
AND LECAP_NO = #{lecapNo}

UPDATE COURSE_REGIST
	SET EVAL_YN_CD = 'Y'
WHERE STD_NO = #{stdNo}
AND LECAP_NO = #{lecapNo}

mapper.select:
SELECT B.SUB_NO, C.SUB_NM, C.SUB_CRD, D.COM_CD_NM AS CRS_CLASSF_CD, B.LECAP_NO, A.CRS_SCR, B.LECAP_YR, B.LECAP_SEM, E.DEPT_NM FROM COURSE_REGIST A JOIN LECTURE_APLY B ON A.LECAP_NO = B.LECAP_NO JOIN SUBJECT C ON B.SUB_NO = C.SUB_NO JOIN COM_CODE D ON C.CRS_CLASSF_CD = D.COM_CD JOIN DEPARTMENT E ON C.DEPT_CD = E.DEPT_CD WHERE 1=1
AND B.LECAP_YR = #{lecapYr}
AND B.LECAP_SEM = #{lecapSem}
