mapper.insert:

	SELECT seq_study_calendar.NEXTVAL FROM dual

INSERT INTO STUDYCALENDAR(
	  cal_no
	, cal_ttl
	, cal_cn
	, cal_bgng_dt
	, cal_end_dt
	, cal_color
	, study_no
)VALUES(
	  #{calNo}
	, #{calTtl}
	, #{calCn}
	, #{calBgngDt}
	, #{calEndDt}
	, #{calColor}
	, #{studyNo}
)

mapper.update:
UPDATE STUDYCALENDAR
SET
	  cal_ttl = #{calTtl}
	, cal_cn = #{calCn}
	, cal_bgng_dt = #{calBgngDt}
	, cal_end_dt = #{calEndDt}
	, cal_color = #{calColor}
WHERE cal_no = #{calNo}

mapper.select:
select msg_no from unreadmessage
	where study_no = #{studyNo} 
	and unread_mem = #{unreadMem}

mapper.update:
	update chatmessage
    set
    	unread_mem_cnt = unread_mem_cnt - 1
    where study_no = #{studyno}
    and msg_no = #{msgno}

update STUDYMEMBER
set
	UNREAD_MSG_CNT = 0
where STD_NO = #{stdNo}
and study_no = #{studyNo}

 		update joinstatus
		    set aprv_stts_cd = 'appv'
		where join_no = #{joinNo}

 		update joinstatus
		    set aprv_stts_cd = 'rej'
		where join_no = #{joinNo}

mapper.select:
SELECT b.* FROM( SELECT a.*, row_number() over (order by a.stbo_no desc) rnum FROM( SELECT a.stbo_no, a.study_no, b.study_name, a.stbo_title, a.stbo_content, a.stbo_writer, a.stbo_regdate, a.stbo_read_cnt FROM studyboard a JOIN studygroup b ON a.study_no = b.study_no WHERE 1=1
ORDER BY a.stbo_no DESC ) a ) b
WHERE b.rnum >= #{startRow} AND b.rnum <= #{endRow}

mapper.update:
update studyboard
set stbo_title = #{stboTitle}, stbo_content = #{stboContent}
where stbo_no = #{stboNo}
